<template>
  <n-layout-header bordered>
    <n-grid :x-gap="12" :y-gap="8" cols="18 s:14 m:12" item-responsive responsive="screen">
      <n-gi offset="1" span="17 s:13 m:2">
        <router-link to="/home">
          <n-image
              width="100"
              src="data:image/png;base64,"
              preview-disabled
          />
        </router-link>
      </n-gi>

      <n-gi offset="0 s:0 m:6" span="18 s:14 m:2" class="align-items-right">
        <n-dropdown :options="options" @select="handleSelect">
          <n-button quaternary>
            Hello and welcome back! &nbsp;<n-text type="primary"><b>{{ name }}</b></n-text>
          </n-button>
        </n-dropdown>
      </n-gi>
    </n-grid>
  </n-layout-header>
</template>

<script>
import {h} from "vue"
import {useRouter} from 'vue-router'
import {
  Person20Filled as UserIcon,
  Edit20Regular as EditIcon,
  ArrowCircleRight20Regular as LogoutIcon
} from "@vicons/fluent";
import {
  NLayoutHeader,
  NGrid,
  NGi,
  NImage,
  NIcon,
  NButton,
  NDropdown,
  NText
} from 'naive-ui'

const renderIcon = (icon) => {
  return () => {
    return h(NIcon, null, {
      default: () => h(icon)
    });
  };
};

export default {
  components: {
    UserIcon,
    EditIcon,
    LogoutIcon,
    NLayoutHeader,
    NGrid,
    NGi,
    NImage,
    NIcon,
    NButton,
    NDropdown,
    NText
  },
  setup() {
    const router = useRouter()
    const first_name = JSON.parse(localStorage.getItem('user')).first_name
    const last_name = JSON.parse(localStorage.getItem('user')).last_name
    return {
      name: first_name.charAt(0).toUpperCase() + first_name.slice(1).toLowerCase() + " " + last_name.charAt(0).toUpperCase() + last_name.slice(1).toLowerCase(),
      options: [
        {
          label: "Edit Profile",
          key: "editProfile",
          icon: renderIcon(EditIcon)
        },
        {
          label: "Logout",
          key: "logout",
          icon: renderIcon(LogoutIcon)
        }
      ],
      handleSelect(key) {
        switch (key) {
          case 'logout':
            localStorage.clear()
            router.push('/')
            break
          case 'editProfile':
            console.log('asdfasdf')
            router.push('/user/update/' + JSON.parse(localStorage.getItem('user')).id)
            break
        }
      }
    }
  },
  name: "NavigationComponent"
}
</script>

<style scoped>
.n-layout-header {
  background: rgba(252, 252, 252, 1);
  padding: 24px;
}
</style>